import React from "react";
import Card from "../components/Card";
import firebase from "firebase/compat";
import AuthContext from "../context/AuthContext";
import { ActivityIndicator } from "react-native";
const MyListings = (props) => {
  const dataRef = firebase.firestore().collection("listing");
  const [listings, setListing] = useState([]);
  const [isLoading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  const getData = () => {
    dataRef.onSnapshot((querySnapshot) => {
      const data = [];

      querySnapshot.forEach((doc) => {
        const { images, price, title } = doc.data();
        data.push({
          id: doc.id,
          images,
          price,
          title,
        });
      });

      setListing(data);
      setLoading(false);
      if (!listings) setError(true);
    });
  };
  console.log(error);
  useEffect(() => {
    setLoading(true);
    getData();
  }, []);
  return (
    <ActivityIndicator animating={isLoading} size="large" color={"#fc5c65"} />
  );
};

export default MyListings;
